mapper.insert:
INSERT INTO STORAGE(ID, RECORD_START, VIDEO_PATH)
      SELECT #{id}, #{record_start}, #{video_path}
      FROM DUAL
      WHERE NOT EXISTS (
         SELECT 1
         FROM STORAGE
         WHERE VIDEO_PATH = #{video_path}
      )

mapper.update:
UPDATE STORAGE
SET CONFIRMED = 1
WHERE VIDEO_PATH = #{video_path}
